import React, {useState, useEffect} from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import './Tab.scss';

import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';

import AboutMe from "./components/AboutMe";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Database from "./components/Database";
import Table  from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";

function App() {

  document.body.classList.add('body-style');
    const [merchants, setMerchants] = useState(false);

    useEffect(() => {
        getMerchant();
    }, []);

    function getMerchant() {
        fetch('http://localhost:3001')
          .then(response => {
            return response.text();
          })
          .then(data => {
            setMerchants(data);
          });
      }

      function createMerchant() {
        let name = prompt('Enter merchant name');
        let email = prompt('Enter merchant email');

        fetch('http://localhost:3001/merchants', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({name, email}),
        })
          .then(response => {
            return response.text();
          })
          .then(data => {
            alert(data);
            getMerchant();
          });
      }

      function deleteMerchant(id) {
        // let id = prompt('Enter merchant id');

        fetch(`http://localhost:3001/merchants/${id}`, {
          method: 'DELETE',
        })
          .then(response => {
            return response.text();
          })
          .then(data => {
            alert(data);
            getMerchant();
          });
      }

      function editMerchant(id) {
        // let id = prompt('Enter merchant id');
        let name = prompt('Enter merchant name');
        let email = prompt('Enter merchant email');

        // console.log(id);

        fetch(`http://localhost:3001/merchants/${id}`, {
          
          headers: {
            'Content-Type': 'application/json',
          },
          method: 'PUT', 
          body: JSON.stringify({name,email}),
        })
          .then(response => {
            return response.text();
          })
          .then(data => {
            alert(data);
            getMerchant();
          });
      }

      let tbl = [
        { id: "1", name: "Anom", email: "Male" },
        { id: "2", name: "Megha", email: "Female" },
        { id: "3", name: "Subham", email: "Male"},
      ];

      function convTable(merchants) {
        let table = JSON.parse(merchants);
        tbl = table;
      }

      return (
        <ul defaultActiveKey="about"
        justify
        className="tab-grp nav nav-tabs myClass">
        <ul eventKey="about" title="About Me" className="nav-item ">
          <li class="active"><a href="#aboutme" data-toggle="tab"><AboutMe /></a></li>
          <li class="active"><a href="#projects" data-toggle="tab"><Projects /></a></li>
          <li class="active"><a href="#contact" data-toggle="tab"><Contact /></a></li>
          <li class="active"><a href="#database" data-toggle="tab">
          <div className="buttons"><Button onClick={createMerchant}>Add Merchant</Button></div>
          <h3>Table of Merchants</h3>
          {document.addEventListener('DOMContentLoaded', convTable(merchants))}
          <div className="table-responsive">
          <Table className="table table-dark">
            <thead>
              <tr>
                <th>ID</th><th>Name</th><th>Email</th><th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {tbl && tbl.map((val, key) => {
                return (
                  <tr key={val.id}>
                    <td>{val.id}</td>
                    <td>{val.name}</td>
                    <td>{val.email}</td>
                    <td className="flex"><Button onClick={() => {deleteMerchant(val.id)}}>Delete</Button><Button onClick={() => {editMerchant(val.id)}}>Edit</Button></td>
                  </tr>
                )})}
            </tbody>
          </Table>
          </div>
            </a></li>
        </ul>
      </ul>
      );
}

export default App;
