import React, {useState, useEffect} from 'react';
function App() {
    const [merchants, setMerchants] = useState(false);

    useEffect(() => {
        getMerchant();
    }, []);

    function getMerchant() {
        fetch('http://localhost:3001')
          .then(response => {
            return response.text();
          })
          .then(data => {
            setMerchants(data);
          });
      }
    
      function createMerchant() {
        let name = prompt('Enter merchant name');
        let email = prompt('Enter merchant email');
    
        fetch('http://localhost:3001/merchants', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({name, email}),
        })
          .then(response => {
            return response.text();
          })
          .then(data => {
            alert(data);
            getMerchant();
          });
      }
    
      function deleteMerchant() {
        let id = prompt('Enter merchant id');
    
        fetch(`http://localhost:3001/merchants/${id}`, {
          method: 'DELETE',
        })
          .then(response => {
            return response.text();
          })
          .then(data => {
            alert(data);
            getMerchant();
          });
      }
    
      function formatMerchants(table){
        table = JSON.parse(table);
        console.log(table.length);

        let text = "<div>";
        table.forEach((m) => {
          text += m.name;
          text += m.email;
        });

        text += "</div>";
        // let text = "";
        // for (const x in table) {
        //   text += table[x] + ", ";
        // }   
        // return table;
      }

      return (
        <div>
          {merchants ? formatMerchants(merchants) : 'There is no merchant data available'}
          <br />
          <button onClick={createMerchant}>Add</button>
          <br />
          <button onClick={deleteMerchant}>Delete</button>
        </div>
      );
}

export default App;
