import React, {useState, useEffect} from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';

import AboutMe from "./components/AboutMe";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Database from "./components/Database";
import Table  from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";

function App() {

  document.body.classList.add('body-style');
    const [merchants, setMerchants] = useState(false);

    useEffect(() => {
        getMerchant();
    }, []);

    function getMerchant() {
        fetch('http://localhost:3001')
          .then(response => {
            return response.text();
          })
          .then(data => {
            setMerchants(data);
          });
      }

      function createMerchant() {
        let name = prompt('Enter merchant name');
        let email = prompt('Enter merchant email');

        fetch('http://localhost:3001/merchants', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({name, email}),
        })
          .then(response => {
            return response.text();
          })
          .then(data => {
            alert(data);
            getMerchant();
          });
      }

      function deleteMerchant(id) {
        // let id = prompt('Enter merchant id');

        fetch(`http://localhost:3001/merchants/${id}`, {
          method: 'DELETE',
        })
          .then(response => {
            return response.text();
          })
          .then(data => {
            alert(data);
            getMerchant();
          });
      }

      let tbl = [
        { id: "1", name: "Anom", email: "Male" },
        { id: "2", name: "Megha", email: "Female" },
        { id: "3", name: "Subham", email: "Male"},
      ];

      function convTable(merchants) {
        // console.log(merchants);
        let table = JSON.parse(merchants);
        // console.log(table[0]);
        console.log("converted table to json");
        tbl = table;
      }

      function onDelete() {

      }
      return (
        <Tabs defaultActiveKey="about"
        justify
        className="tab-grp">
        <Tab eventKey="about" title="About Me">
          <AboutMe />
        </Tab>
        <Tab eventKey="projects" title="Projects">
          <Projects />
        </Tab>
        <Tab eventKey="contact" title="Contact">
          <Contact />
        </Tab>
        <Tab eventKey="database" title="Database">
        <div>
          <button onClick={createMerchant}>Add</button>
          <br />
          <button onClick={deleteMerchant}>Delete</button>
          <br />
          <h3>Table of Merchants</h3>
          {document.addEventListener('DOMContentLoaded', convTable(merchants))}
          <div class="table-responsive">
          <Table class="table table-dark">
            <thead>
              <tr>
                <th>ID</th><th>Name</th><th>Email</th><th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {console.log("map " + tbl)}
              {tbl && tbl.map((val, key) => {
                return (
                  <tr key={key}>
                    <td>{val.id}</td>
                    <td>{val.name}</td>
                    <td>{val.email}</td>
                    <td class="flex"><Button onClick={() => {deleteMerchant(val.id)}} >Delete</Button><Button>Edit</Button></td>
                  </tr>
                )})}
            </tbody>
          </Table>
          </div>
        </div>
        </Tab>
      </Tabs>
      );
}

export default App;
