import React, {useState, useEffect} from 'react';
import '../node_modules/bootstrap';
import axios from 'axios';

function App() {

    // constructor() {
    //   super();
    //   this.state = {
    //     data: []
    //   }
    // }

    // componentDidMount() {

    //   axios.get("http://localhost:3001/merchants").then(response => {
    //     this.setState({ data : response.data });
    //   })
    // }
    
    const [merchants, setMerchants] = useState(false);

    useEffect(() => {
        getMerchant();
    }, []);

    function getMerchant() {
        fetch('http://localhost:3001')
          .then(response => {
            return response.text();
          })
          .then(data => {
            setMerchants(data);
          });
      }
    
      function createMerchant() {
        let name = prompt('Enter merchant name');
        let email = prompt('Enter merchant email');
    
        fetch('http://localhost:3001/merchants', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({name, email}),
        })
          .then(response => {
            return response.text();
          })
          .then(data => {
            alert(data);
            getMerchant();
          });
      }
    
      function deleteMerchant() {
        let id = prompt('Enter merchant id');
    
        fetch(`http://localhost:3001/merchants/${id}`, {
          method: 'DELETE',
        })
          .then(response => {
            return response.text();
          })
          .then(data => {
            alert(data);
            getMerchant();
          });
      }
    
      // function formatMerchants(table){
      //   table = JSON.parse(table);
      //   console.log(table.length);

      //   let text = "";
      //   table.forEach((m) => {
      //     text += "<tr>";
      //     text += "<td>";
      //     text += m.name;
      //     text += "</td>";
      //     text += "<td>";
      //     text += m.email;
      //     text += "</td>";
      //     text += "</tr>";
      //   });

        
      //   console.log(text);
      //   // let text = "";
      //   // for (const x in table) {
      //   //   text += table[x] + ", ";
      //   // }   
      //   // return table;
      // }

      return (
        <div>
          <button onClick={createMerchant}>Add</button>
          <br />
          <button onClick={deleteMerchant}>Delete</button>
          <br />
          <h3>Table of Merchants</h3>
          <table>
            <thead>
              <tr>
                <th>ID</th><th>Name</th><th>Email</th>
              </tr>
            </thead>
            <tbody>
              {this.state.data.map((result) => {
                return (
                  <tr>
                    <td>{result.id}</td>
                    <td>{result.name}</td>
                    <td>{result.email}</td>
                  </tr>
                )})}
            </tbody>
            {/* {merchants ? merchants : 'There is no merchant data available'} */}
          </table>
        </div>
      );
}

export default App;
